    constructor(route) {
        this.route = route;
        this.editPersonal = false;
        this.editAddress = false;
        this.client = {
            name: "",
            phone: "",
            email: "",
            passwd: "",
            login: "",
            date_of_birth: "",
            document: "",
            address: {
                street: "",
                city: "",
