                country: "",
            }
        };
        this.owner = {
            name: "",
            phone: "",
            email: "",
            passwd: "",
            login: "",
            date_of_birth: "",
            document: "",
        };
        this.authToken = localStorage.getItem('authToken');
        this.authTokenOwner = localStorage.getItem('authOwner');
    }
    ngOnInit() {
        let teirep = localStorage.getItem("authOwner");
        let periet = localStorage.getItem("authToken");
