export class ProfileComponent {
    constructor(route) {
        this.route = route;
        this.client = {
            name: "",
            phone: "",
            email: "",
            passwd: "",
            login: "",
            date_of_birth: "",
            document: "",
        };
        this.owner = {
            name: "",
            phone: "",
            email: "",
            passwd: "",
            login: "",
            date_of_birth: "",
            document: "",
